    function releasable(address token) public view virtual returns (uint256) {
        return vestedAmount(token, uint64(block.timestamp)) - released(token);
    }

    /**
     * @dev Getter for the amount of releasable native token.
     */
    function releasable() public view virtual returns (uint256) {
        return vestedAmount(uint64(block.timestamp)) - released();
    }

    /**
     * @dev Getter for the amount of already released `token` tokens. `token` should be the address of an
     * IERC20 contract.
     */
    function released(address token) public view virtual returns (uint256) {
        return _erc20Released[token];
    }

    /**
     * @dev Getter for the amount of `token` tokens that can be withdrawn at the current timestamp. `token` should be the
     * address of an IERC20 contract.
